dataSample.sql:
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder` ( `id_appagebuilder` int(11) NOT NULL AUTO_INCREMENT, `id_appagebuilder_positions` int(11) NOT NULL, `hook_name` varchar(255) DEFAULT NULL, `id_appagebuilder_shortcode` int(11) NOT NULL, PRIMARY KEY (`id_appagebuilder`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_details` ( `id_appagebuilder_details` int(11) NOT NULL AUTO_INCREMENT, `plist_key` varchar(255) DEFAULT NULL, `name` varchar(255) DEFAULT NULL, `class_detail` varchar(255) DEFAULT NULL, `params` text, `type` tinyint(1) DEFAULT NULL, `active` tinyint(1) DEFAULT NULL, `url_img_preview` varchar(255) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_details`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_details_shop` ( `id_appagebuilder_details` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_details`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_lang` ( `id_appagebuilder` int(11) NOT NULL AUTO_INCREMENT, `id_lang` int(10) unsigned NOT NULL, `params` mediumtext, PRIMARY KEY (`id_appagebuilder`,`id_lang`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_page` ( `id_product` int(11) unsigned NOT NULL, `id_category` int(11) unsigned NOT NULL, `page` varchar(255) NOT NULL, `id_shop` int(10) unsigned NOT NULL, PRIMARY KEY (`id_product`,`id_category`,`id_shop`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_positions` ( `id_appagebuilder_positions` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) NOT NULL, `position` varchar(255) NOT NULL, `position_key` varchar(255) NOT NULL, `params` text, PRIMARY KEY (`id_appagebuilder_positions`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_positions_shop` ( `id_appagebuilder_positions` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, PRIMARY KEY (`id_appagebuilder_positions`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_products` ( `id_appagebuilder_products` int(11) NOT NULL AUTO_INCREMENT, `plist_key` varchar(255) DEFAULT NULL, `name` varchar(255) DEFAULT NULL, `class` varchar(255) DEFAULT NULL, `params` text, `type` tinyint(1) DEFAULT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_products`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_products_shop` ( `id_appagebuilder_products` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_products`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_profiles` ( `id_appagebuilder_profiles` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT NULL, `group_box` varchar(255) DEFAULT NULL, `profile_key` varchar(255) DEFAULT NULL, `page` varchar(255) DEFAULT NULL, `params` text, `header` int(11) unsigned NOT NULL, `content` int(11) unsigned NOT NULL, `footer` int(11) unsigned NOT NULL, `product` int(11) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_profiles`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_profiles_lang` ( `id_appagebuilder_profiles` int(11) NOT NULL AUTO_INCREMENT, `id_lang` int(10) unsigned NOT NULL, `friendly_url` varchar(255) DEFAULT NULL, `meta_title` varchar(255) DEFAULT NULL, `meta_description` varchar(255) DEFAULT NULL, `meta_keywords` varchar(255) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_profiles`,`id_lang`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_profiles_shop` ( `id_appagebuilder_profiles` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_profiles`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_shop` ( `id_appagebuilder` int(11) NOT NULL AUTO_INCREMENT, `id_shop` int(10) unsigned NOT NULL, PRIMARY KEY (`id_appagebuilder`,`id_shop`) ) ENGINE=ENGINE_TYPE AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_shortcode` ( `id_appagebuilder_shortcode` int(11) NOT NULL AUTO_INCREMENT, `id_appagebuilder` int(11) unsigned DEFAULT NULL, `shortcode_key` varchar(255) NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_shortcode`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_shortcode_lang` ( `id_appagebuilder_shortcode` int(11) unsigned NOT NULL, `id_lang` int(10) unsigned NOT NULL, `shortcode_name` text NOT NULL, PRIMARY KEY (`id_appagebuilder_shortcode`,`id_lang`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_appagebuilder_shortcode_shop` ( `id_appagebuilder_shortcode` int(11) unsigned NOT NULL, `id_shop` int(10) unsigned NOT NULL, `active` tinyint(1) DEFAULT NULL, PRIMARY KEY (`id_appagebuilder_shortcode`,`id_shop`) ) ENGINE=ENGINE_TYPE DEFAULT CHARSET=utf8;
